        [TestMethod]
        public void EncryptDecryptTest1()
        {
            for (int arraySize = 1; arraySize < 32 * 2 + 1; arraySize++)
            {
                using (var target = new AsymmetricEncryptionProvider(AsymmetricPrivateEncryptionKeyTest.First))
                {
                    byte[] originalValue = Enumerable.Range(0, arraySize).Select(value => (byte)value).ToArray();

                    byte[] encrypted = target.Encrypt(originalValue);

                    byte[] decrypted = target.Decrypt(encrypted);

                    Assert.IsTrue(EnumerableComparer.Compare(originalValue, decrypted));
                }
            }
        }

